import { takaro, data, axios } from '@takaro/helpers';
async function main() {
  const { pog } = data;
  const playerRes = await takaro.player.playerControllerGetOne(data.player.id);
  const steamId = playerRes.data.data.steamId;
  const xboxLiveId = playerRes.data.data.xboxLiveId;
  const name = playerRes.data.data.name;
  const key = data.module.userConfig.votekey;

  let voteId = `username=${name}`;
  if (steamId.startsWith("7656")) { voteId = `steamid=${steamId}`; }


  const res = await axios.get(`https://7daystodie-servers.com/api/?object=votes&element=claim&key=${key}&${voteId}`);
  if (res.data == 0) { data.player.pm(data.module.userConfig.didntvote); }
  if (res.data == 2) { data.player.pm(data.module.userConfig.alreadyvoted); }
  if (res.data == 1) {

    const claimresponse = await axios.post(`https://7daystodie-servers.com/api/?action=post&object=votes&element=claim&key=${key}&${voteId}`);
    if (claimresponse.data == 1) {
      if (data.module.userConfig.votednow.toLowerCase() !== 'none' && data.module.userConfig.votednow.toLowerCase() !== '"none"') { data.player.pm(data.module.userConfig.votednow); }
      if (data.module.userConfig.publicMessage.toLowerCase() !== 'none' && data.module.userConfig.publicMessage.toLowerCase() !== '"none"') {
        const originalMessage = data.module.userConfig.publicMessage;
        const modifiedMessage = originalMessage.replace(/\{name\}/g, name);
        await takaro.gameserver.gameServerControllerExecuteCommand(data.gameServerId, { command: `say "${modifiedMessage}"` });
      }
      await takaro.playerOnGameserver.playerOnGameServerControllerAddCurrency(data.gameServerId, pog.playerId, { currency: data.module.userConfig.currency });
      for (const stuff of data.module.userConfig.giveStuff) {
        if (voteId.startsWith(`steamid`)) {
          await takaro.gameserver.gameServerControllerExecuteCommand(data.gameServerId, { command: `giveplus Steam_${steamId} ${stuff}` });
        } else {
          await takaro.gameserver.gameServerControllerExecuteCommand(data.gameServerId, { command: `giveplus ${xboxLiveId} ${stuff}` });
        }
      }
    }
  }
}



await main();
